mapper.select:
SELECT *
FROM cstudio_copytoenvironment
WHERE site = #{site}
AND state = #{state}
AND scheduleddate > #{now}
ORDER BY scheduleddate ASC

SELECT *
FROM cstudio_copytoenvironment
WHERE site = #{site}
AND state in

    #{state}

AND scheduleddate > #{now}
ORDER BY scheduleddate ASC, id asc

mapper.update:
UPDATE cstudio_copytoenvironment
SET state = #{canceledState}
WHERE site = #{site}
AND path = #{path}
AND state = #{state}
AND scheduleddate > #{now}

UPDATE cstudio_copytoenvironment
SET state = #{canceledState}
WHERE site = #{site}
AND state = #{state}
AND scheduleddate > #{now}
AND path IN

    #{path}

UPDATE cstudio_copytoenvironment
SET state = #{canceledState}
WHERE id = #{id}
AND site = #{site}
AND path = #{path}

mapper.select:
SELECT *
FROM cstudio_copytoenvironment
WHERE site = #{site}
AND state = #{state}
AND environment = #{environment}
AND scheduleddate <= #{now}
ORDER BY scheduleddate ASC

SELECT count(1) FROM cstudio_copytoenvironment
WHERE site = #{site}
AND path = #{path}
AND state = #{state}

SELECT count(1) FROM cstudio_copytoenvironment
WHERE site = #{site}
AND environment = #{environment}
AND path = #{path}
AND state = #{state}

mapper.update:
UPDATE cstudio_copytoenvironment
SET state = #{state}
WHERE id = #{id}
