mapper.select:
SELECT cd.site, cd.source_path, cd.target_path, cd.type
FROM cstudio_DEPENDENCY cd
WHERE cd.source_path = #{sourcePath} AND cd.site = #{site}

SELECT site, source_path, target_path, type
FROM cstudio_DEPENDENCY
WHERE source_path = #{sourcePath} AND site = #{site}

SELECT cd.site, cd.source_path, cd.target_path, cd.type
FROM cstudio_DEPENDENCY cd
WHERE cd.target_path = #{targetPath} AND cd.site = #{site}

SELECT site, source_path, target_path, type
FROM cstudio_DEPENDENCY
WHERE target_path = #{targetPath} AND site = #{site}

mapper.delete:
DELETE
FROM cstudio_DEPENDENCY
WHERE source_path = #{path} AND site = #{site}

mapper.select:
SELECT cd.site, cd.source_path, cd.target_path, cd.type
FROM cstudio_DEPENDENCY cd
WHERE cd.source_path = #{sourcePath} AND cd.site = #{site} AND cd.type = #{type}

SELECT site, source_path, target_path, type
FROM cstudio_DEPENDENCY
WHERE source_path = #{sourcePath} AND site = #{site} AND type = #{type}

mapper.delete:
DELETE FROM cstudio_DEPENDENCY
WHERE site = #{site}
AND (source_path = #{path} OR target_path = #{path})

mapper.select:
SELECT target_path
FROM cstudio_DEPENDENCY
WHERE site = #{site}
AND source_path IN

    #{path}

SELECT target_path
FROM (cstudio_DEPENDENCY cd INNER JOIN cstudio_objectstate co ON cd.target_path = co.path AND cd.site = co.site)
WHERE cd.site = #{site}
AND cd.source_path IN

    #{path}

AND co.state IN

    #{state}

SELECT source_path
FROM cstudio_DEPENDENCY
WHERE site = #{site}
AND target_path in

    #{path}

SELECT cd1.source_path, cd1.target_path
FROM (cstudio_DEPENDENCY cd1 INNER JOIN cstudio_objectstate co1 ON cd1.target_path = co1.path AND cd1.site = co1.site)
WHERE cd1.site = #{site}
AND cd1.source_path IN

    #{path}

AND co1.state IN

    #{state}

UNION
SELECT cd2.source_path, cd2.target_path
FROM (cstudio_DEPENDENCY cd2 INNER JOIN cstudio_objectstate co2 ON cd2.target_path = co2.path AND cd2.site = co2.site)
WHERE cd2.site = #{site}
AND cd2.source_path IN

    #{path}

AND co2.state IN

    #{state}

AND

    REGEXP_LIKE(cd2.target_path, #{pattern})

SELECT cd1.source_path, cd1.target_path
FROM (cstudio_DEPENDENCY cd1 INNER JOIN cstudio_objectstate co1 ON cd1.target_path = co1.path AND cd1.site = co1.site)
WHERE cd1.site = #{site}
AND cd1.source_path IN

    #{path}

AND co1.state IN

    #{state}

UNION
SELECT cd2.source_path, cd2.target_path
FROM (cstudio_DEPENDENCY cd2 INNER JOIN cstudio_objectstate co2 ON cd2.target_path = co2.path AND cd2.site = co2.site)
WHERE cd2.site = #{site}
AND cd2.source_path IN

    #{path}

AND co2.state IN

    #{state}

AND

    cd2.target_path SIMILAR TO #{pattern}

SELECT cd1.source_path, cd1.target_path
FROM (cstudio_DEPENDENCY cd1 INNER JOIN cstudio_objectstate co1 ON cd1.target_path = co1.path AND cd1.site = co1.site)
WHERE cd1.site = #{site}
AND cd1.source_path IN

    #{path}

AND co1.state IN

    #{state}

UNION
SELECT cd2.source_path, cd2.target_path
FROM (cstudio_DEPENDENCY cd2 INNER JOIN cstudio_objectstate co2 ON cd2.target_path = co2.path AND cd2.site = co2.site)
WHERE cd2.site = #{site}
AND cd2.source_path IN

    #{path}

AND co2.state IN

    #{state}

AND

    cd2.target_path RLIKE #{pattern}
