mapper.select:
SELECT * FROM ( SELECT a.*, ROWNUM rnum FROM ( SELECT dsh1.* FROM cstudio_deploymentsynchistory dsh1 INNER JOIN ( SELECT dsh2.site, dsh2.path, dsh2.target, MAX(dsh2.syncdate) syncdate FROM cstudio_deploymentsynchistory dsh2 WHERE dsh2.site = #{site} AND dsh2.syncdate BETWEEN #{from_date} AND #{to_date} GROUP BY dsh2.site, dsh2.path, dsh2.target, TRUNC(dsh2.syncdate)) dsh3 ON dsh1.path = dsh3.path and dsh1.target = dsh3.target and dsh1.syncdate = dsh3.syncdate WHERE dsh1.site = #{site}
AND dsh1.contenttypeclass=#{filter}
ORDER BY dsh1.syncdate desc ) a WHERE #{limit} >= ROWNUM) WHERE rnum >= 0

SELECT TOP (#{limit}) dsh1.*
FROM cstudio_deploymentsynchistory as dsh1 INNER JOIN (
SELECT dsh2.site, dsh2.path, dsh2.target, MAX(dsh2.syncdate) as syncdate
FROM cstudio_deploymentsynchistory as dsh2
WHERE dsh2.site = #{site}
AND dsh2.syncdate BETWEEN #{from_date} AND #{to_date}
GROUP BY dsh2.site, dsh2.path, dsh2.target, convert(varchar(10), dsh2.syncdate)) as dsh3
ON dsh1.path = dsh3.path and dsh1.target = dsh3.target and dsh1.syncdate = dsh3.syncdate
WHERE dsh1.site = #{site}

    AND dsh1.contenttypeclass=#{filter}

ORDER BY dsh1.syncdate desc

SELECT dsh1.*
FROM cstudio_deploymentsynchistory as dsh1 INNER JOIN (
SELECT dsh2.site, dsh2.path, dsh2.target, MAX(dsh2.syncdate) as syncdate
FROM cstudio_deploymentsynchistory as dsh2
WHERE dsh2.site = #{site}
AND dsh2.syncdate BETWEEN #{from_date} AND #{to_date}
GROUP BY dsh2.site, dsh2.path, dsh2.target, DATE(dsh2.syncdate)) as dsh3
ON dsh1.path = dsh3.path and dsh1.target = dsh3.target and dsh1.syncdate = dsh3.syncdate
WHERE dsh1.site = #{site}

    AND dsh1.contenttypeclass=#{filter}

ORDER BY dsh1.syncdate desc
FETCH FIRST #{limit} ROWS ONLY

SELECT dsh1.*
FROM cstudio_deploymentsynchistory as dsh1 INNER JOIN (
SELECT dsh2.site, dsh2.path, dsh2.target, MAX(dsh2.syncdate) as syncdate
FROM cstudio_deploymentsynchistory as dsh2
WHERE dsh2.site = #{site}
AND dsh2.syncdate BETWEEN #{from_date} AND #{to_date}
GROUP BY dsh2.site, dsh2.path, dsh2.target, DATE(dsh2.syncdate)) as dsh3
ON dsh1.path = dsh3.path and dsh1.target = dsh3.target and dsh1.syncdate = dsh3.syncdate
WHERE dsh1.site = #{site}

    AND dsh1.contenttypeclass=#{filter}

ORDER BY dsh1.syncdate desc LIMIT #{limit}
