mapper.update:
UPDATE item_metadata
path=#{newPath}, name=#{name}, modified=#{modified}, modifier=#{modifier}, owner=#{owner}, creator=#{creator}, firstname=#{firstName}, lastname=#{lastName}, lockowner=#{lockOwner}, email=#{email}, renamed=#{renamed}, oldurl=#{oldUrl}, deleteurl=#{deleteUrl}, imagewidth=#{imageWidth}, imageheight=#{imageHeight}, approvedby=#{approvedBy}, submittedby=#{submittedBy}, submittedfordeletion=#{submittedForDeletion}, sendemail=#{sendEmail}, submissioncomment=#{submissionComment}, launchdate=#{launchDate}, commit_id=#{commitId}, submittedtoenvironment=#{submittedToEnvironment}
WHERE site=#{site} AND path=#{path}

UPDATE item_metadata
path=#{newPath}, name=#{name}, modified=#{modified}, modifier=#{modifier}, owner=#{owner}, creator=#{creator}, firstname=#{firstName}, lastname=#{lastName}, lockowner=#{lockOwner}, email=#{email}, renamed=#{renamed}, oldurl=#{oldUrl}, deleteurl=#{deleteUrl}, imagewidth=#{imageWidth}, imageheight=#{imageHeight}, approvedby=#{approvedBy}, submittedby=#{submittedBy}, submittedfordeletion=#{submittedForDeletion}, sendemail=#{sendEmail}, submissioncomment=#{submissionComment}, launchdate=#{launchDate}, commit_id=#{commitId}, submittedtoenvironment=#{submittedToEnvironment}
WHERE site=#{site} AND commit_id=#{commitId}

UPDATE item_metadata SET
lockowner=#{lockOwner}
WHERE site=#{site}
AND path=#{path}

UPDATE item_metadata
SET path = #{newPath}
WHERE site = #{site}
AND path = #{oldPath}

UPDATE item_metadata
SET commit_id = #{commitId}
WHERE site = #{site}
AND path = #{path}

mapper.select:
SELECT COUNT(1) FROM item_metadata
WHERE site = #{siteId}
AND renamed = 1
AND oldurl = #{path}

select count (1) from item_metadata where site=#{siteId}

    AND path = #{path}


    AND modifier = #{modifier}



        AND modified BETWEEN #{dateFrom} AND #{dateTo}


        AND modified >= #{dateFrom}


        AND #{dateTo} >= modified

select * from item_metadata where site=#{siteId}

    AND path RLIKE #{path}


    AND modifier = #{modifier}



        AND modified BETWEEN #{dateFrom} AND #{dateTo}


        AND modified >= #{dateFrom}


        AND #{dateTo} >= modified




        ORDER BY ${sort} ${order}


        ORDER BY ${sort}


        ORDER BY modified ${order}


        ORDER BY modified DESC


LIMIT #{offset}, #{limit}
