mapper.select:
SELECT object_id, site, path, state, system_processing
FROM item_state
WHERE state in

    #{state}

and site=#{site}

SELECT object_id, site, path, state, system_processing
FROM item_state
WHERE site = #{site} AND path = #{path}

mapper.update:
UPDATE item_state
SET system_processing = #{systemProcessing}
WHERE site = #{site} AND path = #{path}

UPDATE item_state
SET system_processing = #{systemProcessing}
WHERE site = #{site} AND path IN

    #{path}

UPDATE item_state
SET state = #{state}, system_processing = #{systemProcessing}
WHERE object_id = #{objectId}

mapper.select:
SELECT object_id, site, path, state, system_processing
FROM item_state
WHERE site = #{site}
AND path in

    #{path}

mapper.update:
UPDATE item_state
SET state = #{state}
WHERE site = #{site}
AND path in

    #{path}

UPDATE item_state
SET path = #{newPath}
WHERE site = #{site}
AND path = #{oldPath}

mapper.delete:
DELETE FROM item_state
WHERE site = #{site}
AND path = BINARY #{path}

DELETE FROM item_state
WHERE site = #{site}
AND path like  #{path}

mapper.update:
UPDATE item_state
SET state = #{state}, system_processing = 0
WHERE site = #{siteId}

mapper.select:
SELECT COUNT(1) FROM item_state
WHERE site = #{siteId}
AND path = #{path}
AND state IN

    #{state}

mapper.update:
UPDATE item_state itstate INNER JOIN item_metadata itmeta ON (itstate.site = itmeta.site AND itstate.path = itmeta.path)
SET itstate.state = #{state}, itstate.system_processing = 0
WHERE itstate.site = #{siteId}
AND itmeta.commit_id = #{commitId}

mapper.select:
SELECT is1.path as path
FROM item_state is1
WHERE is1.site = #{site}
AND is1.path IN

    #{path}

AND is1.state IN

    #{state}

UNION
SELECT is2.path as path
FROM (item_state is2 INNER JOIN item_metadata im2 ON is2.path = im2.path AND is2.site = im2.site)
WHERE is2.site = #{site}
AND im2.renamed > 0
AND is2.path IN

    #{path}

AND is2.state IN

    #{state}
