mapper.select:
SELECT *
FROM publish_request
WHERE site = #{site}
AND state = #{state}
AND scheduleddate > #{now}
ORDER BY scheduleddate ASC

SELECT *
FROM publish_request
WHERE site = #{site}
AND state in

    #{state}

AND scheduleddate > #{now}
ORDER BY scheduleddate ASC, id asc

mapper.update:
UPDATE publish_request
SET state = #{canceledState}
WHERE site = #{site}
AND path = #{path}
AND state = #{state}
AND scheduleddate > #{now}

UPDATE publish_request
SET state = #{canceledState}
WHERE id = #{id}
AND site = #{site}
AND path = #{path}

mapper.select:
SELECT *
FROM publish_request
WHERE site = #{site}
AND state = #{state}
AND environment = #{environment}
AND scheduleddate <= #{now}
ORDER BY scheduleddate ASC

SELECT count(1) FROM publish_request
WHERE site = #{site}
AND path = #{path}
AND state = #{state}

SELECT count(1) FROM publish_request
WHERE site = #{site}
AND environment = #{environment}
AND path = #{path}
AND state = #{state}

mapper.update:
UPDATE publish_request
SET state = #{state}
WHERE id = #{id}

mapper.select:
SELECT *
FROM publish_request
WHERE site = #{site}
AND scheduleddate <= #{now}
AND state in

    #{state}

ORDER BY scheduleddate ASC
LIMIT 0, 1

SELECT count(1)
FROM publish_request
WHERE site = #{site}
AND scheduleddate <= #{now}
AND state = #{state}
ORDER BY scheduleddate ASC
LIMIT 0, 1

SELECT *
FROM publish_request
WHERE site = #{site}
AND state = #{state}
AND scheduleddate <= #{now}
ORDER BY scheduleddate DESC
LIMIT 0, 1
