mapper.select:
SELECT cu.username as username, cu.firstname as firstname, cu.lastname as lastname, cu.email as email, cg.name as groupname, cs.site_id as siteid, cs.name as sitename FROM cstudio_user cu LEFT JOIN cstudio_usergroup cug on cu.username = cug.username LEFT JOIN cstudio_group cg on cug.groupId = cg.id LEFT JOIN cstudio_site cs on cg.site_id=cs.id WHERE cu.username in
#{username}
ORDER BY username, siteid, groupname

SELECT cu.username as username, cu.firstname as firstname, cu.lastname as lastname, cu.email as email, cg.name as groupname, cs.site_id as siteid, cs.name as sitename FROM cstudio_user cu INNER JOIN cstudio_usergroup cug on cu.username = cug.username INNER JOIN cstudio_group cg on cug.groupId = cg.id INNER JOIN cstudio_site cs on cg.site_id=cs.id WHERE cu.username in
#{username}
ORDER BY username

mapper.update:
UPDATE cstudio_user

    firstname = #{firstname},
    lastname = #{lastname},
    email = #{email}

WHERE username = #{username}

UPDATE cstudio_user
SET enabled = #{enabled}
WHERE username = #{username}

mapper.select:
SELECT cg.name AS group_name, cg.description AS description, cs.site_id AS site_id
FROM cstudio_group cg INNER JOIN cstudio_site cs on cg.site_id = cs.id
WHERE cs.site_id = #{siteId} AND cg.name = #{groupName}

SELECT cg.*
FROM cstudio_group cg INNER JOIN cstudio_site cs on cg.site_id = cs.id
WHERE cs.site_id = #{siteId} AND cg.name = #{groupName}

SELECT cs.site_id AS site_id, cs.name AS site_name, cg.name AS group_name, cg.description AS group_description
FROM cstudio_group cg INNER JOIN cstudio_site cs on cg.site_id = cs.id
LIMIT #{start},#{num}

SELECT cu.*
FROM cstudio_group cg INNER JOIN cstudio_site cs ON cg.site_id = cs.id
  INNER JOIN cstudio_usergroup cug ON cg.id = cug.groupid
  INNER JOIN cstudio_user cu ON cug.username = cu.username
WHERE cs.site_id = #{siteId}
AND cg.name = #{groupName}
LIMIT #{start},#{number}

mapper.update:
UPDATE cstudio_group
SET description = #{description}
WHERE site_id = #{siteId}
AND name = #{groupName}

UPDATE cstudio_user
SET password = #{password}
WHERE username = #{username}

mapper.select:
SELECT COUNT (1)
FROM cstudio_group cg INNER JOIN cstudio_site cs ON cg.site_id = cs.id
INNER JOIN cstudio_usergroup cug ON cg.id = cug.groupid
INNER JOIN cstudio_user cu ON cug.username = cu.username
WHERE cs.site_id = #{siteId} AND cu.username = #{username}
AND cg.name = #{groupName}

SELECT COUNT (1)
FROM cstudio_user cu
WHERE cu.username = #{username}

SELECT COUNT (1)
FROM cstudio_group cg INNER JOIN cstudio_site cs ON cg.site_id = cs.id
WHERE cs.site_id = #{siteId} AND cg.name = #{groupName}
