mapper.select:
SELECT i.*, u1.username AS lock_owner, u2.username AS creator, u3.username AS modifier, 0 as children_count FROM item i LEFT OUTER JOIN user u1 ON i.locked_by = u1.id LEFT OUTER JOIN user u2 ON i.created_by = u2.id LEFT OUTER JOIN user u3 ON i.last_modified_by = u3.id WHERE i.site_id = #{siteId} AND i.path = #{ldPath} UNION (SELECT i1.*, u1.username AS lock_owner, u2.username AS creator, u3.username AS modifier, COUNT(DISTINCT i3.id) as children_count FROM item i1 left outer join item i3 on i1.id = i3.parent_id LEFT OUTER JOIN user u1 ON i1.locked_by = u1.id LEFT OUTER JOIN user u2 ON i1.created_by = u2.id LEFT OUTER JOIN user u3 ON i1.last_modified_by = u3.id WHERE i1.site_id = #{siteId} AND i1.path RLIKE('^${path}/[^/]+/index\.xml$|^${path}/(?!index\.xml)[^/]+$') AND CONCAT(i1.path, '/index.xml') NOT IN (SELECT i2.path FROM item i2 WHERE i2.site_id = #{siteId} AND i2.path RLIKE('^${path}/[^/]+/index\.xml$')) AND i1.path NOT LIKE '%/${ldName}'
AND i1.locale_code = #{localeCode}
AND (i1.path LIKE '%${keyword}%' OR i1.label LIKE '%${keyword}%')
AND i1.ignored = 0 AND (i3.ignored = 0 or i3.ignored is null) AND (i3.system_type <> #{systemTypeFolder} or not exists (select * from item i10 where i10.site_id = i3.site_id and i10.path = concat(i3.path, '/index.xml')))
AND i1.path NOT RLIKE('${exclude}')
GROUP BY i1.id
ORDER BY i1.label ${order}, i1.path ${order}
ORDER BY IF(FIELD(i1.system_type, 'folder')=0,1,0), FIELD(i1.system_type, 'folder'), i1.label ${order}, i1.path ${order}
ORDER BY i1.label ${order}, i1.path ${order}
LIMIT #{offset},#{limit})

SELECT COUNT(1) FROM item WHERE site_id = #{siteId}
AND path RLIKE('^${path}/[^/]+/index\.xml$|^${path}/(?!index\.xml)[^/]+$')
AND CONCAT(path, '/index.xml') NOT IN
(SELECT i2.path FROM item i2 WHERE i2.site_id = #{siteId}
AND i2.path RLIKE('^${path}/[^/]+/index\.xml$'))
AND path NOT LIKE '%/${ldName}'

    AND locale_code = #{localeCode}


    AND (path LIKE '%${keyword}%' OR label LIKE '%${keyword}%')

AND ignored = 0


        AND path NOT RLIKE('${exclude}')

