ORDER BY i1.label ${order}, i1.path ${order}
) LIMIT #{offset},#{limit})

SELECT COUNT(1) FROM item WHERE site_id = #{siteId} AND parent_id = #{parentId}
AND path NOT LIKE '%/${ldName}'

    AND locale_code = #{localeCode}


    AND (path LIKE '%${keyword}%' OR label LIKE '%${keyword}%')

AND ignored = 0


        AND path NOT RLIKE('${exclude}')

SELECT i.*, u1.username AS lock_owner, u2.username AS creator, u3.username AS modifier, COUNT(DISTINCT i3.id) AS children_count FROM item i LEFT OUTER JOIN item i3 ON i.id = i3.parent_id LEFT OUTER JOIN user u1 ON i.locked_by = u1.id LEFT OUTER JOIN user u2 ON i.created_by = u2.id LEFT OUTER JOIN user u3 ON i.last_modified_by = u3.id WHERE i.id IN
#{id}
