#{id}
AND CONCAT(i3.path, '/index.xml') NOT IN (SELECT i4.path FROM item i4 WHERE i4.site_id = i3.site_id AND i4.path RLIKE(CONCAT('^', i3.path, '/index\.xml$'))) AND (i6.ignored = 0 OR i6.ignored IS NULL) AND (i6.system_type <> #{systemTypeFolder} OR NOT EXISTS (SELECT * FROM item i11 WHERE i11.site_id = i6.site_id AND i11.path = CONCAT(i6.path, '/index.xml'))) GROUP BY i3.id)

