mapper.select:
SELECT cu.username AS username, cu.first_name AS first_name, cu.last_name AS last_name, cu.email AS email, cu.externally_managed AS externally_managed, cg.group_name AS group_name FROM `user` cu LEFT JOIN group_user cug ON cu.id = cug.user_id LEFT JOIN `group` cg ON cug.group_id = cg.id WHERE cu.username IN
#{username}
ORDER BY username, group_name

SELECT cu.username AS username, cu.first_name AS firstname, cu.last_name AS lastname, cu.email AS email, cu.externally_managed AS externally_managed, cg.group_name AS groupname FROM `user` cu INNER JOIN group_user cug ON cu.id = cug.user_id INNER JOIN `group` cg ON cug.group_id = cg.id WHERE cu.username IN
#{username}
ORDER BY username

SELECT cg.group_name AS group_name, cg.group_description AS description
FROM `group` cg
WHERE cg.group_name = #{groupName}

SELECT cg.*
FROM `group` cg
WHERE  cg.group_name = #{groupName}

SELECT cg.group_name AS group_name, cg.group_description AS group_description FROM `group` cg WHERE cg.id IN
#{groupid}
ORDER BY cg.group_name

SELECT cg.group_name AS group_name, cg.group_description AS group_description, cu.username AS username, cu.first_name AS first_name, cu.last_name AS last_name, cu.externally_managed AS externally_managed, cu.email AS email FROM `group` cg LEFT JOIN group_user cug ON cg.id = cug.group_id LEFT JOIN `user` cu ON cug.user_id = cu.id WHERE cg.id IN
#{groupid}
ORDER BY cg.group_name

SELECT cu.*
FROM `group` cg
  INNER JOIN group_user cug ON cg.id = cug.group_id
  INNER JOIN `user` cu ON cug.user_id = cu.id
WHERE cg.group_name = #{groupName}
LIMIT #{start},#{number}

SELECT COUNT(1)
FROM `group` cg
INNER JOIN group_user cug ON cg.id = cug.group_id
INNER JOIN `user` cu ON cug.user_id = cu.id
WHERE cu.username = #{username}
AND cg.group_name = #{groupName}

SELECT COUNT(1)
FROM `user` cu
WHERE cu.username = #{username}

SELECT COUNT(1)
FROM `group` cg
WHERE  cg.group_name = #{groupName}

mapper.delete:
DELETE FROM refresh_token
WHERE
    TIMESTAMPDIFF(MINUTE, created_on, NOW()) > #{sessionTimeout}

OR
    user_id in

        #{inactiveUsers}
