mapper.update:
UPDATE item,
    (SELECT i.id
    FROM item i INNER JOIN item_publish_item ipi ON i.id = ipi.item_id
    INNER JOIN publish_item pi ON ipi.publish_item_id = pi.id
    WHERE pi.package_id = #{packageId}
    ) AS updates
SET item.state = (item.state | #{onStatesBitMap}) & ~#{offStatesBitMap}
WHERE item.id = updates.id

mapper.select:
SELECT * FROM ( SELECT s.id as site_id, ( SELECT pp.id AS id FROM publish_package pp WHERE pp.approval_state IN
#{approvalState}
AND (pp.package_state & #{packageState}) = #{packageState} AND (pp.schedule IS NULL OR pp.schedule < NOW()) AND pp.site_id = s.id ORDER BY pp.schedule, pp.submitted_on LIMIT 1 ) AS id FROM site s WHERE s.deleted = 0 AND s.system = 0 AND s.state IN
#{siteState}
) AS tbl WHERE id IS NOT NULL

SELECT *
FROM publish_package
WHERE id = #{packageId}
and site_id = #{siteId}

SELECT *
FROM publish_package
WHERE site_id = #{siteId}
AND id IN

            #{packageId}

mapper.update:
UPDATE publish_package
    SET package_state = #{cancelledState}
WHERE approval_state IN

                #{approvalState}

AND (package_state & #{packageState}) > 0
AND site_id = #{siteId}

    AND target = #{target}

UPDATE item i
        INNER JOIN item_publish_item ipi ON i.id = ipi.item_id
        INNER JOIN publish_item pi ON ipi.publish_item_id = pi.id
    SET i.state = (i.state | #{onStatesBitMap})
WHERE pi.package_id = #{packageId}
AND i.id IN (
        SELECT ipi.item_id
        FROM item_publish_item ipi
            INNER JOIN publish_item pi ON ipi.publish_item_id = pi.id
            INNER JOIN publish_package pp ON pi.package_id = pp.id
        WHERE pp.package_state = #{packageState}
        AND pp.approval_state IN

            #{approvalState}


            AND pp.schedule IS NOT null


            AND pp.target = #{target}

)

UPDATE item, (
        SELECT ipi.item_id, (CASE
                                WHEN (pi.publish_state & #{itemSuccessState}) > 0
                                THEN #{successOnStatesBitMap}
                                ELSE 0
                                END
                                ) AS maskOn,
                            (CASE
                                WHEN (pi.publish_state & #{itemSuccessState}) > 0
                                THEN #{successOffStatesBitMap}
                                ELSE #{failureOffStatesBitMap}
                                END
                            ) AS maskOff
        FROM publish_package pp
            INNER JOIN publish_item pi ON pp.id = pi.package_id
            INNER JOIN item_publish_item ipi ON pi.id = ipi.publish_item_id
        WHERE pp.id = #{packageId}
    ) AS updates
SET item.state = (item.state | updates.maskOn) & ~updates.maskOff
WHERE item.id = updates.item_id

mapper.select:
SELECT pi.*, ipi.item_id
FROM publish_item pi
    INNER JOIN publish_package pp ON pi.package_id = pp.id
    INNER JOIN site s ON pp.site_id = s.id
    LEFT OUTER JOIN item_publish_item ipi ON pi.id = ipi.publish_item_id
WHERE pi.package_id = #{packageId}
AND s.site_id = #{siteId}

    LIMIT #{offset},#{limit}

SELECT COUNT(1)
FROM publish_item pi
INNER JOIN publish_package pp ON pi.package_id = pp.id
INNER JOIN site s ON pp.site_id = s.id
WHERE pi.package_id = #{packageId}
AND s.site_id = #{siteId}

mapper.update:
UPDATE publish_item
SET publish_state = (publish_state | #{onStatesBitMap}) & ~#{offStatesBitMap}
WHERE package_id = #{packageId}

UPDATE publish_item pi INNER JOIN (
(SELECT #{item.id} AS id, #{item.publishState} AS state, #{item.liveError} AS liveError, #{item.stagingError} AS stagingError)
) AS updates ON pi.id = updates.id SET pi.publish_state = updates.state, pi.live_error = updates.liveError, pi.staging_error = updates.stagingError

mapper.sql:
SELECT pp.*
FROM publish_package pp
    INNER JOIN site s ON pp.site_id = s.id
WHERE s.site_id = #{siteId}
AND (pp.package_state & #{packageState}) > 0
AND pp.approval_state IN

        #{approvalState}

AND EXISTS (
    SELECT 1
    FROM publish_item pi
    WHERE pi.package_id = pp.id
    AND (pi.path IN

            #{path}



        OR


                pi.path like #{pattern}


    )
)

sql.where:
s.site_id = #{siteId}
AND (pp.package_state & #{packageState}) > 0

    AND IFNULL(rev.username, sub.username) = #{approver}


    AND pp.published_on >= #{dateFrom}


    AND pp.published_on <= #{dateTo}


    AND pp.target = #{target}
